mapper.select:
SELECT 	a.*,a.insert_author ui,a.id idi,#{login_user_id} uii,a.id uiii,'0' as ss	FROM article a

    and a.title = #{title}
    and a.type = #{type}
    and a.category = #{category}
    and a.state = #{state}
    and a.status = #{status}
    and a.insert_author = #{insert_author}
    and find_in_set(a.insert_author, #{insert_authors})

order by insert_time desc

SELECT 	a.*,a.insert_author ui,a.id idi,#{login_user_id} uii,a.id uiii,'0' as ss FROM article a

    and id = #{id}
    and a.title = #{title}
    and a.type = #{type}
    and a.state = #{state}
    and a.status = #{status}

SELECT a.*, a.user_id ui
FROM
article_comment a

    and a.state = #{state}
    and a.article_id = #{article_id}
    and a.user_id = #{user_id}

order by insert_time desc

mapper.update:
UPDATE article a
a.update_time = now(), a.title = #{title}, a.type = #{type}, a.state = #{state}, a.status = #{status}, a.content = #{content}, a.update_author = #{update_author},
WHERE a.id = #{id};
