mapper.select:
SELECT 	a.*,a.insert_author ui,a.id idi,a.insert_author uii	FROM article a

    and a.title = #{title}
    and a.type = #{type}
    and a.state = #{state}
    and a.status = #{status}

mapper.update:
UPDATE article a
a.update_time = now(), a.title = #{title}, a.type = #{type}, a.state = #{state}, a.status = #{status}, a.content = #{content}, a.update_author = #{update_author},
WHERE a.id = #{id};

update `article` set star = star + 1 where id = #{article_id};
update article_info set star = 1
, a.self_star = 1
 where id = #{article_info_id};

update `article` set star = star - 1 where id = #{article_id};
update article_info set star = 0
, a.self_star = 0
 where article_id = #{article_id} and user_id = #{user_id};

update `article` set collect = collect + 1 where id = #{article_id};
update article_info set collect = 1
, a.self_watch = 1
 where id = #{article_info_id};

update `article` set collect = collect - 1 where id = #{article_id};
update article_info set collect = 0
, a.self_watch = 0
 where article_id = #{article_id} and user_id = #{user_id};
