mapper.update:
UPDATE lock_info
update_time = now(), `lock_no` = #{lock_no}, `address` = #{address}, latitude = #{latitude}, longitude = #{longitude}, qr_code_no = #{qr_code_no}, device_no = #{device_no}, state = #{state}, status = #{status}, update_author = #{update_author}, lock_mac = #{lock_mac}, lock_pwd = #{lock_pwd}, lock_key = #{lock_key}, hospital = #{hospital}, user_id = #{user_id},
WHERE id = #{id}

mapper.select:
select a.*,a.user_id ui from lock_info a where a.status = 0

    and a.`state` = #{state}
    and a.lock_no = #{lock_no}
    and a.device_no = #{device_no}
    and a.qr_code_no = #{qr_code_no}
    and a.lock_mac = #{lock_mac}
    and a.hospital = #{hospital}
    and a.user_id = #{user_id}
