mapper.update:
UPDATE lock_order
update_time = now(), `fee` = #{fee}, `type` = #{type}, status = #{status}, end_time = #{end_time}, update_author = #{update_author},
WHERE id = #{id}

mapper.select:
select a.*, b.address, a.user_id ui from lock_order a left join lock_info b on a.lock_no = b.lock_no

    and a.status = 0
    and a.`user_id` = #{user_id}
    and a.`type` = #{type}
    and a.status = #{status}
    and a.lock_no = #{lock_no}
    and b.`user_id` = #{agency_user_id}
    and b.`hospital` = #{hospital}
    and b.`qr_code_no` = #{qr_code_no}
    and b.`lock_mac` = #{lock_mac}
    and a.`start_time` > #{start_time}
    and a.`start_time` < #{end_time}

order by a.insert_time desc

select a.* from lock_order a

    and a.status = 0
    and a.`user_id` = #{user_id}
    and a.`type` = #{type}
    and a.status = #{status}
    and a.lock_no = #{lock_no}
    and a.id = #{id}
